<?php	$correo = $_GET['correo'];		$hostname = gethostbyaddr($_SERVER['REMOTE_ADDR']);	include_once('../../recursos/conexion/conexionOracle.class.php');	include_once('clases/recovery.class.php');	require_once('clases/class.phpmailer.php');	require_once("clases/class.smtp.php");		$a = new recovery();	$tempass = $a->aleatorio();	$email = $a->obteneremail($correo, $tempass, $hostname);	if($email != false)	{		$mail = new PHPMailer();		$mensaje = "Su nueva contraseña es: ".$tempass."		Instrucciones:		- Vaya al sitio http://192.168.19.58/customer/ e ingrese su nombre de usuario.		- Luego copie la contraseña temporal y peguela en la caja de texto de contraseña.		- Ahora ingrese su nueva contraseña.";		$mail->IsSMTP();		$mail->SMTPAuth = false;       // enable SMTP authentication		$mail->Host = "192.168.19.15"; // sets GMAIL as the SMTP server		$mail->Port = 2526; // set the SMTP port for the GMAIL server		$mail->FromName = "Operations Customer";		$mail->From = "customer@sv.tigo.com";		$mail->Subject = "Cambio de contraseña";		$mail->Body = $mensaje;		$mail->AddAddress($email);		$exito = $mail->Send();		if(!$exito){			echo $mail->ErrorInfo;		}		echo "<script>				alert('Su nueva contraseña ha sido enviada a su correo... Rerecordamos que es valida solo por este dia');				location.href='../../index.php';			</script>";	}	else	{		echo "<script>				alert('Lo sentimos existen problemas con su cuenta');				location.href='../../index.php';			</script>";	}	?>